import logo from './logo.svg';
import React, { useState } from 'react';
import './App.css';
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import firebaseConfig from './FirebaseConfig';

firebase. initializeApp(firebaseConfig);

function App() {

  const [user,setUser]= useState({
    isSignnedIn: false,
    name: '',
    email:'',
    photo:''
  });

  const provider = new firebase.auth.GoogleAuthProvider();
  const handleSignIn= ()=> {
    firebase.auth().signInWithPopup(provider)
    .then(res => {   
       console.log(res);
      const {email,name,picture}= res.additionalUserInfo.profile
      const signnedInUser = {
        isSignnedIn : true,
        name: name,
        email: email,
        photo: picture,
      }

    setUser(signnedInUser);
    console.log(email, name, picture);
    })
    .catch (err => {
      console.log(err);
      console.log(err.message);

    })
  }

  const handleSignOut = () => {
    console.log('Signing out...');

  }
  return (
    <div className="App">

    {
      user.isSignnedIn ?<button onClick={handleSignIn} >Sign out</button> : <button onClick={handleSignIn} >Sign In</button>

      }
      {
        user.isSignnedIn && <div>
        <p>Welcome, {user.name}</p>
        <p>Your Email: {user.email}</p>
        <img src={user.photo} alt="broken pic" />

        </div>
      }




    </div>

  );
}

export default App;
